import React from "react";
import { Element } from "react-scroll";
import styled from "styled-components";
import { SectionTitle } from "../../components/sectionTitle";
import AboutImgUrl from "../../assets/pictures/MoreImages/background_shoe.svg";
import {

    createMuiTheme,
    responsiveFontSizes,
    MuiThemeProvider,
    Typography
} from "@material-ui/core";

let theme = createMuiTheme();
theme = responsiveFontSizes(theme);



const MoreAboutContainer = styled(Element)`
    min-height: 500px;
    display: flex;
    flex-direction: column;
    align-items: center;
    font-family: 'Arimo', sans-serif;
    margin-top: 200px;
`;

const AboutContainer = styled.div`
    display: flex;
    max-width: 260px;
    margin-top: 20px;
    justify-content: center;
`;

const AboutText = styled.p`
    font-size: 20px;
    color: #2F2F2F; 
    font-weight: normal;
    line-height: 1.4;
`;

const AboutImg = styled.img`
    width: 300px;
    height: 200px;
`;

export function MoreAboutSneakPeek(props) {
    return <MoreAboutContainer>
        <SectionTitle>
            About Sneak Peek
        </SectionTitle>
        <AboutContainer>
            <AboutText item xs={12} sm={6} md={4}>
                <Typography variant="subtitle1" gutterBottom>
                Sneak Peek is an online destination to discover upcoming Men's & Women's Sneakers from
                the best fashion brands, stores, and boutiques around the world. At Sneak Peek we offer
                the most unique way of viewing and saving dates for your favorite upcoming sneaker releases!
                </Typography>
                Sneak Peek is an online destination to discover upcoming Men's & Women's Sneakers from
                the best fashion brands, stores, and boutiques around the world. At Sneak Peek we offer
                the most unique way of viewing and saving dates for your favorite upcoming sneaker releases!
                {<br />} {<br/>}
                Our aim is to create a place for customers to discover their favorite sneakers.
                The vision that embodies Sneak Peek is to provide our customers with the best experience and to 
                build a strong platform in the mobile application market.
            </AboutText>
        </AboutContainer>
    </MoreAboutContainer>
}
