import React from "react";
import { Element } from "react-scroll";
import styled from "styled-components";
import { SectionTitle } from "../../components/sectionTitle";
import AboutImgUrl from "../../assets/pictures/MoreImages/background_shoe.svg";
import {

    createMuiTheme,
    responsiveFontSizes,
    MuiThemeProvider,
    Typography
} from "@material-ui/core";

let theme = createMuiTheme();
theme = responsiveFontSizes(theme);

const MoreAboutContainer = styled(Element)`
    min-height: 500px;
    display: flex;
    flex-direction: column;
    align-items: center;
    font-family: 'Arimo', sans-serif;
    margin-top: 200px;
    text-align: center;
`;

const AboutImg = styled.img`
    width: 300px;
    height: 200px;
`;

const heading = "About Sneak Peek";
const line1 = "Sneak Peek is an online destination to discover upcoming Men's & Womens's Sneakers";
const line2 = "from the best fashion brands, stores, and boutiques around the world. At Sneak Peek we offer the";
const line3 = "most unique way of viewing and saving dates for your favorite sneaker releases!";
const line4 = "Our aim is to create a place for customers to discover their favorite sneakers.";
const line5 = "The vision that embodies Sneak Peek is to provide our customers with the best experience and to build a strong platform in the mobile application market.";

export function MoreAboutSneakPeek(props) {
    return <MoreAboutContainer>
            <MuiThemeProvider theme={theme}>
                <Typography style={{ fontWeight: "bold", fontFamily: 'Arimo' }} variant="h2" gutterBottom>
                    {heading}
                </Typography>

                <Typography style={{ fontFamily: 'Arimo' }} variant="h6" gutterBottom>
                    {line1}
                </Typography>

                <Typography style={{ fontFamily: 'Arimo' }} variant="h6" gutterBottom>
                    {line2}
                </Typography>

                <Typography style={{ fontFamily: 'Arimo', paddingBottom: '50px' }} variant="h6" gutterBottom>
                    {line3}
                </Typography>

                <Typography style={{ fontFamily: 'Arimo' }} variant="h6" gutterBottom>
                    {line4}
                </Typography>

                <Typography style={{ fontFamily: 'Arimo' }} variant="h6" gutterBottom>
                    {line5}
                </Typography>
            </MuiThemeProvider>
    </MoreAboutContainer>
}
